function draw(canvas, ctx, width, height) {
  // 绘制背景
  function background(color) {
    ctx.fillStyle = color;
    ctx.fillRect(0, 0, width, height);
  }

  // 全局变量
  let mouseX = width / 2;
  let mouseY = height / 2;
  const size = width / 10;
  const cnt = 50;
  const circleArr = [];
  for (let i = 0; i < cnt; i++) {
    circleArr.push({
      x: mouseX,
      y: mouseX
    });
  }

  function update() {
    // shift array
    for (let i = 0; i < cnt - 1; i++) {
      circleArr[i].x = circleArr[i + 1].x;
      circleArr[i].y = circleArr[i + 1].y;
    }

    circleArr[cnt - 1].x = mouseX;
    circleArr[cnt - 1].y = mouseY;

    // draw circle
    background("black");
    circleArr.forEach((item, index) => {
      const c = 255 - (cnt - index);
      ctx.fillStyle = `rgb(${c},${c},${c})`;
      ctx.beginPath();
      ctx.arc(item.x, item.y, index, 0, 2 * Math.PI);
      ctx.fill();
    });
  }

  canvas.onmousemove = e => {
    mouseX = e.offsetX;
    mouseY = e.offsetY;
  };

  const timer = setInterval(update, 10);
  return timer;
}
